import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table, Container, Row, Col, Button, Card } from "react-bootstrap";

const PerformanceReport = () => {
  const [report, setReport] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/performance-report")
      .then(response => setReport(response.data))
      .catch(error => console.error("Error fetching performance report:", error));
  }, []);

  const handleDownload = () => {
    alert("PDF Downloading...");  // Placeholder for actual PDF generation
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="p-4 shadow">
            <h2 className="text-center">Performance Report</h2>
            <div className="table-responsive">
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Department</th>
                    <th>Performance</th>
                  </tr>
                </thead>
                <tbody>
                  {report.length > 0 ? (
                    report.map(emp => (
                      <tr key={emp.id}>
                        <td>{emp.id}</td>
                        <td>{emp.name}</td>
                        <td>{emp.department}</td>
                        <td>{emp.performance}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="4" className="text-center">No Report Data</td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </div>
            <div className="text-center mt-3">
              <Button onClick={handleDownload} className="btn btn-primary">Download PDF</Button>
            </div>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default PerformanceReport;
