import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Container, Card, Row, Col } from "react-bootstrap";

const AddEmployee = () => {
  const [employee, setEmployee] = useState({
    name: "",
    department: "",
    performance: "",
  });

  const handleChange = (e) => {
    setEmployee({ ...employee, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:8080/employees", employee)
      .then(() => alert("Employee Added Successfully"))
      .catch((error) => console.error("Error adding employee:", error));
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="p-4 shadow">
            <h2 className="text-center">Add Employee</h2>
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" name="name" onChange={handleChange} required />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Department</Form.Label>
                <Form.Control type="text" name="department" onChange={handleChange} required />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Performance Rating (A-E)</Form.Label>
                <Form.Select name="performance" onChange={handleChange} required>
                  <option value="">Select Rating</option>
                  <option value="A">A - Excellent</option>
                  <option value="B">B - Good</option>
                  <option value="C">C - Average</option>
                  <option value="D">D - Below Average</option>
                  <option value="E">E - Poor</option>
                </Form.Select>
              </Form.Group>

              <Button type="submit" className="w-100">Add Employee</Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AddEmployee;
