import React, { useEffect, useState } from "react";
import axios from "axios";

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [editEmployee, setEditEmployee] = useState(null);
    const [updatedName, setUpdatedName] = useState("");

    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = () => {
        axios.get("http://localhost:8080/employees")
            .then(response => setEmployees(response.data))
            .catch(error => console.error("Error fetching employees:", error));
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:8080/employees/${id}`)
            .then(() => fetchEmployees()) // Refresh the list after deletion
            .catch(error => console.error("Error deleting employee:", error));
    };

    const handleUpdate = (id) => {
        axios.put(`http://localhost:8080/employees/${id}`, { employeeName: updatedName })
            .then(() => {
                setEditEmployee(null);
                setUpdatedName("");
                fetchEmployees(); // Refresh list after update
            })
            .catch(error => console.error("Error updating employee:", error));
    };

    return (
        <div>
            <h2>Employee List</h2>
            <ul>
                {employees.map(emp => (
                    <li key={emp.employeeId}>
                        {editEmployee === emp.employeeId ? (
                            <>
                                <input
                                    type="text"
                                    value={updatedName}
                                    onChange={(e) => setUpdatedName(e.target.value)}
                                    placeholder="Enter new name"
                                />
                                <button onClick={() => handleUpdate(emp.employeeId)}>Save</button>
                                <button onClick={() => setEditEmployee(null)}>Cancel</button>
                            </>
                        ) : (
                            <>
                                {emp.employeeName}
                                <button onClick={() => { setEditEmployee(emp.employeeId); setUpdatedName(emp.employeeName); }}>
                                    Edit
                                </button>
                                <button onClick={() => handleDelete(emp.employeeId)}>Delete</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EmployeeList;
